        domain: '.' + BB.cur_domain,
        path: "/forum/",
};

input.script:
(function () {
        if ((typeof (window.opera) != "undefined" && window.opera.version() < 12.10)) {
                var el = document.getElementById('old-browser-warn');
                el.innerHTML = 'Вы используете устаревший браузер. Сайт может отображаться некорректно.';
                el.style.display = '';
        }
})();

